import React, { useState } from 'react';
import Modal from 'react-modal';
import EnlargedImageModal from './EnlargedImageModal';

const ProjectCard = ({ title, date, imageUrl, techStack, link, descriptionImage, description }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
    setCurrentImageIndex(0);
  };

  const enlargeImage = (index) => {
    setCurrentImageIndex(index);
    document.body.classList.add('image-enlarged'); // Add a class to the body to enlarge the image
  };

  const shrinkImage = () => {
    document.body.classList.remove('image-enlarged'); // Remove the class to shrink the image
  };

  const nextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % descriptionImage.length);
  };

  const prevImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex - 1 + descriptionImage.length) % descriptionImage.length);
  };

  return (
    <div className="project-card">
      <h3 className="project-title">{title}</h3>
      <p className="project-date">{date}</p>
      <img src={imageUrl} alt={title} className="project-image" />
      <p className="project-techstack">{techStack}</p>
      <p className="project-link">{link}</p>
      <button className="see-more-button" onClick={toggleModal}>
        See More
      </button>
      <Modal isOpen={isModalOpen} onRequestClose={toggleModal}>
        <div className="project-modal-content">
          <h2 className="project-title">{title}</h2>
          <p className="project-date">{date}</p>
          <p className="project-techstack">{techStack}</p>
          <p className="project-link">{link}</p>

          {/* <div className="image-gallery">
            <img src={descriptionImage[currentImageIndex]} alt={`Project ${title} Image`} className="project-gallery-image" />
            <div className="image-navigation">
              <button onClick={prevImage}>Previous</button>
              <button onClick={nextImage}>Next</button>
            </div>
          </div> */}

          <div className="image-gallery">
          {descriptionImage.map((image, index) => (
          <img
            key={index}
            src={image}
            alt={`Project ${title} Image ${index + 1}`}
            className="project-gallery-image"
            onClick={() => enlargeImage(index)}
          />
        ))}
         </div>
          <div className="image-navigation">
            <button onClick={prevImage}>Previous</button>
            <button onClick={nextImage}>Next</button>
          </div>


          <p className="project-description">{description}</p>
          {/* Add more details or content here */}
          <button onClick={toggleModal}>Close</button>
        </div>
      </Modal>

      {isImageModalOpen && (
        <EnlargedImageModal
          imageUrl={descriptionImage[currentImageIndex]}
          handleClose={() => setIsImageModalOpen(false)}
        />
      )}

    </div>
  );
};

export default ProjectCard;
