import React from 'react';
import ProjectCard from '../components/ProjectCard';
import '../styles.css';

const Experience = () => {
  const projects = [
    {
      id: 1,
      title: 'Web Development Internship at TechJoy',
      date: 'July 2023-Dec 2023',
      imageUrl: '/assets/orff/orff.png',
      techStack: 'Tech Stack/Tools Used: JavaScript, React, Python, Django, Cypress Testing, Git, GitHub, ZenHub',
      link: 'Link: https://offroadfunfinder.com/signin',
      description: 'During my internship at TechJoy, I was tasked with working on the Off Road Fun Finder client site, a versatile web and mobile app designed for offroading enthusiasts to discover tracks, access training resources, and stay updated on nationwide events. My focus was primarily on the web version of the app.',
    },
    {
      id: 2,
      title: 'Software Engineering Internship at Drata',
      date: 'May 2023-June 2023',
      imageUrl: 'assets/drata/drata.png',
      description: 'Description of Project 2...',
    },
    {
      id: 3,
      title: 'Blurred Limes: Full-Stack Web Development Project',
      date: 'May 2023',
      imageUrl: 'assets/blurredlimes/blurredlimes.png',
      description: 'Description of Project 3...',
    }
  ];

  const handleSeeMore = (projectId) => {
    // Implement logic to open modal for the selected project
    console.log('See More clicked for project ID:', projectId);
  };

  return (
    <div id="experience" className="section">
      <div className="experience-container">
        <main className="experience-main">
          <div className="experience-content-wrapper">
            <h1 className="experience-text-title">Experience</h1>
            <div className="project-cards-container">
              {projects.map((project) => (
                <ProjectCard
                  key={project.id}
                  title={project.title}
                  date={project.date}
                  imageUrl={project.imageUrl}
                  techStack={project.techStack}
                  techStack={project.techStack}
                  description={project.description}
                  onClick={() => handleSeeMore(project.id)}
                />
              ))}
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default Experience;
